/* global chrome */
import logo from './logo.svg';
import './App.css';
import { useEffect } from 'react';


function grabElements() {
  const element = document.getElementById('productTitle');
  if (element) {
    console.log('Data from element:', element.textContent);
  } else {
    console.log('Element with ID "productTitle" not found');
  }
}

function App() {
  useEffect(() => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      const currentTab = tabs[0];
      chrome.scripting.executeScript(
        {
          target: { tabId: currentTab.id },
          function: grabElements,
        }
      );
    });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and fun away yayyyy to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
